import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

// This is the new line that determines which API to use.
const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

const features = [
  'Participates in School Activities', 'Uses Tutor', 'Prefers Studying Alone',
  'Attends School Regularly', 'Has Part-Time Job', 'Uses Planner for Schoolwork',
  'Gets Nervous Before Exams', 'Prefers Online Classes', 'Takes Notes in Class',
  'Has Quiet Study Environment'
];

function App() {
  const [formData, setFormData] = useState({});
  const [prediction, setPrediction] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleChange = (feature, value) => {
    setFormData(prev => ({ ...prev, [feature]: value }));
  };

  const isFormComplete = () => {
    return features.length === Object.keys(formData).length;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!isFormComplete()) {
      setError("Please answer all questions before predicting.");
      return;
    }

    setLoading(true);
    setError(null);
    setPrediction(null);

    try {
      const featureMap = {
        'Participates in School Activities': 'Participates in School Activities',
        'Uses Tutor': 'Uses a Tutor',
        'Prefers Studying Alone': 'Prefers Studying Alone',
        'Attends School Regularly': 'Attends School Regularly',
        'Has Part-Time Job': 'Has a Part-time Job',
        'Uses Planner for Schoolwork': 'Uses a Planner for Schoolwork',
        'Gets Nervous Before Exams': 'Gets Nervous Before Exams',
        'Prefers Online Classes': 'Prefers Online Classes Over In-person',
        'Takes Notes in Class': 'Takes Notes in Class',
        'Has Quiet Study Environment': 'Has a Quiet Study Environment at Home'
      };

      // The order of features must match the model's training order.
      // This is a safer way to ensure the order is correct.
      const orderedFeatures = [
        'Gender', 'School Type', 'Studies Daily', 'Attends Extra Classes',
        'Uses Online Learning Platforms', 'Has a Fixed Study Schedule',
        'Participates in Group Study', 'Sleeps at Least 7 Hours',
        'Uses Social Media During Study Hours', 'Submits Assignments on Time',
        'Enjoys Reading', 'Participates in School Activities', 'Uses a Tutor',
        'Prefers Studying Alone', 'Attends School Regularly', 'Has a Part-time Job',
        'Uses a Planner for Schoolwork', 'Gets Nervous Before Exams',
        'Prefers Online Classes Over In-person', 'Takes Notes in Class',
        'Has a Quiet Study Environment at Home'
      ];
      
      // For this model, all inputs are binary (Yes=1, No=0).
      // We will create a full feature array with placeholders.
      // NOTE: This assumes default "No" (0) for features not in the form.
      // This part might need adjustment if your model expects all 21 features.
      const allFeatureValues = { ...formData };
      
      // Convert Yes/No from form to 1/0 for the API
      const featureValues = Object.values(allFeatureValues).map(val => parseInt(val));
      
      // THIS IS THE KEY CHANGE: The request now goes to your live Render API
      const response = await axios.post(`${API_URL}/predict`, {
        features: featureValues
      });

      setPrediction(response.data.prediction[0]);

    } catch (err) {
      console.error("Prediction error:", err);
      setError('Failed to get prediction. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleReset = () => {
    setFormData({});
    setPrediction(null);
    setError(null);
    setLoading(false);
  };

  return (
    <div className="App">
      <div className="container mx-auto p-4">
        <header className="text-center my-8">
          <h1 className="text-4xl md:text-5xl font-bold text-white">Student Performance Predictor</h1>
          <p className="text-lg text-gray-200 mt-4">Answer the following questions to predict academic performance.</p>
        </header>

        <main>
          <div className="card bg-white/90 backdrop-blur-md rounded-2xl shadow-lg p-6 md:p-8">
            <form onSubmit={handleSubmit}>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {features.map(feature => (
                  <div key={feature} className="form-group">
                    <label className="block text-gray-700 font-semibold mb-2">{feature}</label>
                    <div className="flex space-x-4">
                      {['Yes', 'No'].map(option => (
                        <div
                          key={option}
                          className={`radio-option flex-1 text-center p-3 rounded-lg border-2 cursor-pointer transition-all duration-200 ${formData[feature] === (option === 'Yes' ? '1' : '0') ? 'selected border-indigo-500 bg-indigo-50 text-indigo-700' : 'border-gray-300'}`}
                          onClick={() => handleChange(feature, option === 'Yes' ? '1' : '0')}
                        >
                          <input
                            type="radio"
                            name={feature}
                            value={option === 'Yes' ? '1' : '0'}
                            checked={formData[feature] === (option === 'Yes' ? '1' : '0')}
                            onChange={() => {}}
                            className="hidden"
                          />
                          <span>{option}</span>
                        </div>
                      ))}
                    </div>
                  </div>
                ))}
              </div>

              <div className="flex justify-center space-x-4 mt-8">
                <button type="submit" className="btn-predict" disabled={loading || !isFormComplete()}>
                  {loading ? 'Predicting...' : 'Predict Performance'}
                </button>
                <button type="button" onClick={handleReset} className="btn-reset">
                  Reset Form
                </button>
              </div>
            </form>

            {error && (
              <div className="result-error mt-6 p-4 bg-red-100 border border-red-400 text-red-700 rounded-lg text-center">
                <p>{error}</p>
              </div>
            )}

            {prediction !== null && (
              <div className={`result mt-6 p-6 rounded-lg text-center text-white text-2xl font-bold ${prediction === 1 ? 'bg-green-500' : 'bg-red-500'}`}>
                {prediction === 1 ? 'Predicted: High Performer' : 'Predicted: Low Performer'}
              </div>
            )}
          </div>
          
          <div className="about-model card bg-white/90 backdrop-blur-md rounded-2xl shadow-lg p-6 md:p-8 mt-8">
            <h2 className="text-2xl font-bold text-gray-800 mb-3">About This Model</h2>
            <p className="text-gray-600">
              This prediction model was trained on data from 310 students using a Random Forest Classifier. It analyzes 21 different factors including study habits, sleep patterns, attendance, and learning preferences to predict academic performance with 74% accuracy on test data and 87% accuracy in cross-validation.
            </p>
          </div>
        </main>
      </div>
    </div>
  );
}

export default App;
